import React from 'react';
import { Route, Redirect } from 'react-router-dom';
import { isAuthenticated } from '../controllers/userController';
import { isAdmin } from '../controllers/authController';

const PrivateRouteAdmin = async ({ component: Component, ...rest }) => {
    let check = await isAdmin(isAuthenticated().token);
    return (
        <Route
            {...rest}
            render={ async props =>
                isAuthenticated() && check ===  "admin" ? (
                    <Component {...props} userPayload={isAuthenticated()} />
                ) : (
                        <Redirect
                            to={{
                                pathname: "/auth",
                                state: { from: props.location }
                            }}
                        />
                    )
            }
        />
    );
}

export default PrivateRouteAdmin;
