import React, { useState, useEffect } from 'react';

function useDebounce(text, delay) {
    // console.log("useDebounce call");
    delay = delay || 500;
    const [debounced, setDebounced] = useState(text);

    useEffect(() => {
    //   console.log("componentDidMount in Debounce");
      const handler = setTimeout(() => {
        setDebounced(text);
      }, delay);

      return () => {
        clearTimeout(handler);
      };
    }, [text, delay]);

    return debounced;
  }

  function App() {
    // console.log("APP");
    const [val, setVal] = useState("Hello Codesandbox");
    const [title, setTitle] = useState("Hello Codesandbox");
    const debouncedVal = useDebounce(val);

    useEffect(() => {
    //   console.log("componentDidMount in Apps");
      if (debouncedVal) {
        // console.log("if true");
        setTitle(debouncedVal);
      }
    }, [debouncedVal]);

    console.log(title)
    return (
        <input
            type="text" name="usersfilter" placeholder="Filter by user" maxLength="240" className="s-input s-input__search js-search-users" id="js-search-users"
            onChange={({ target }) => setVal(target.value)}
        />
    );
}

export default useDebounce
