import React, { Component } from 'react';
import { Link, withRouter } from 'react-router-dom';
import './Header.css';
import brandLogo from '../../../images/logo.png';

import { isAuthenticated, getSignout } from '../../../controllers/userController';
import { getNotifications } from "../../../controllers/notifyController";
import dfimg from '../../../images/solar-system.png';

import ListRestItem from './ListRestItem/ListRestItem';
import FormSerach from './FormSearch/FormSerach';
import UserDropdown from './UserDropDown/UserDropdown';

class Header extends Component {

    constructor() {
        super();

        this.state = {
            toggleMenu: false,
            toggleProducts: false,
            toggleNotify: false,
            uid: null,
            photo: "",
            notifications: [],
            message: ""
        }

        // this.handleToggleMenu = this.handleToggleMenu.bind(this);
    }

    clickRoot = () => {
        this.setState({ toggleMenu: false, toggleSearch: false, toggleProducts: false, toggleNotify: false, toggleUser: false });
    }

    componentDidMount() {
        // document.getElementById("root").addEventListener("click", this.clickRoot);
        if (isAuthenticated()) {
            let uid = isAuthenticated().user._id;
            let token = isAuthenticated().token;
            getNotifications(uid, token)
                .then(res => {
                    this.setState({ notifications: res })
                })
            this.setState({ uid, photo: isAuthenticated().user.photo });

        }
    }

    handleToggleMenu = () => {
        let toggleButton = document.querySelector("a.left-sidebar-toggle");
        let leftSide = document.querySelector(".left-sidebar");

        let { toggleMenu } = this.state;

        if (!toggleMenu) {
            toggleButton.classList.add("topbar-icon-on");
            leftSide.classList.add("topbar-icon-on");
        } else {
            toggleButton.classList.remove("topbar-icon-on");
            leftSide.classList.remove("topbar-icon-on");
        }

        this.setState(function (prevState, props) {
            return { toggleMenu: !prevState.toggleMenu }
        });
    }



    handleToggleProducts = () => {
        let { toggleProducts } = this.state;

        if (!toggleProducts) {
            document.querySelector("#products-popover").style.display = "block";

        } else {
            document.querySelector("#products-popover").style.display = "none";

        }

        this.setState(function (prevState, props) {
            return { toggleProducts: !prevState.toggleProducts }
        })

    }



    handleToggleNotify = () => {
        let { toggleNotify } = this.state;
        if (!toggleNotify) {
            document.querySelector(".list-notify-box").style.display = "block";
        } else {
            document.querySelector(".list-notify-box").style.display = "none";
        }

        this.setState({ toggleNotify: !this.state.toggleNotify });

    }

    handleSignout = () => {
        getSignout()
            .then(res => {
                window.localStorage.removeItem("jwt");
                this.props.history.push("/");
            })
    }

    render() {
        const { uid, photo, notifications } = this.state;
        return (
            <header id="header" className="ps-fixed w-100">

                <div className="container d-flex align-items-center">
                    <div className="brand-logo d-flex align-items-center">
                        <Link to="/">
                            <img src={brandLogo} alt="brand-logo" />
                        </Link>
                    </div>
                    <a className="left-sidebar-toggle d-flex align-items-center justify-content-center"
                        href="#tg"
                        onClick={this.handleToggleMenu}
                    >
                        <span className="ps-absolute"></span>
                    </a>
                    <ul className="menu-horizontal w-100 d-flex align-items-center">
                        <li className="item-top item-top--radius ps-relative">
                            <a
                                href="#product"
                                onClick={this.handleToggleProducts}
                            >
                                Products
                            </a>
                            <div id="products-popover" className="s-popover ps-absolute">
                                <div className="s-popover--arrow"></div>
                                <ul className="list-rest s-anchors">
                                    <ListRestItem
                                        title="Liars ask"
                                        description="Public questions and answers"
                                        href="/admin"
                                    />
                                    <ListRestItem
                                        title="Advance Search"
                                        description="Search with tags, title, date, ..."
                                        href="/advance/search"
                                    />
                                    <ListRestItem
                                        title="Teams"
                                        description="Private questions and answers for your team"
                                        href="#teams"
                                    />
                                    <ListRestItem
                                        title="Enterprise"
                                        description="Private self-hosted questions and answers for your enterprise"
                                        href="#teams"
                                    />
                                    <hr className="oc7" />
                                    <ListRestItem
                                        title="Talent"
                                        description="Hire technical talent"
                                        href="#teams"
                                    />
                                    <ListRestItem
                                        title="Advertising"
                                        description="Reach developers worldwide"
                                        href="#teams"
                                    />
                                </ul>
                            </div>
                        </li>
                        <li className="w-100">
                            <FormSerach />
                        </li>

                        {
                            isAuthenticated() ?
                                (
                                    <>
                                        <li className="item-top item-top--radius ps-relative">
                                            <UserDropdown
                                                uid={uid}
                                                photo={photo}
                                                handleSignout={this.handleSignout}
                                            />
                                        </li>
                                        <li className="item-top">
                                            <div class="notification-box" onClick={this.handleToggleNotify}>
                                                <span class="notification-count">{notifications.length}</span>
                                                <div class="notification-bell">
                                                    <span class="bell-top"></span>
                                                    <span class="bell-middle"></span>
                                                    <span class="bell-bottom"></span>
                                                    <span class="bell-rad"></span>
                                                </div>
                                                <div className="list-notify-box">
                                                    <ul className="">
                                                        {
                                                            notifications.length > 0 ? notifications.map((ntf, i) => {
                                                                return <li className="item-notify">
                                                                    <Link className="w-100 content-notify-item " to="/#">
                                                                        <img style={{ float: "left", marginRight: "10px" }} className="bd50" width="50" height="50" src={ntf.photo || dfimg} alt="avatar" />
                                                                        <span>
                                                                            {ntf.content.length > 100 ? ntf.content.substr(0, 100) : ntf.content} ...
                                                                </span>
                                                                    </Link>
                                                                </li>
                                                            }) : <span style={{ padding: "0 16px" }}>No notification</span>
                                                        }
                                                    </ul>
                                                </div>
                                            </div>
                                        </li>
                                    </>
                            ) : (
                                <li className="item-top item-top--radius ps-relative">
                                    <Link to="/auth">Signin</Link>
                                </li>
                            )
                        }


                        <li className="item-top">
                            <a href="#help">
                                <svg aria-hidden="true" className="svg-icon iconHelp" width="18" height="18" viewBox="0 0 18 18"><path d="M9 1a8 8 0 1 0 0 16A8 8 0 0 0 9 1zm.81 12.13c-.02.71-.55 1.15-1.24 1.13-.66-.02-1.17-.49-1.15-1.2.02-.72.56-1.18 1.22-1.16.7.03 1.2.51 1.17 1.23zM11.77 8c-.3.34-.65.65-1.02.91l-.53.37c-.26.2-.42.43-.5.69a4 4 0 0 0-.09.75c0 .05-.03.16-.18.16H7.88c-.16 0-.18-.1-.18-.15.03-.66.12-1.21.4-1.66.4-.49.88-.9 1.43-1.22.16-.12.28-.25.38-.39a1.34 1.34 0 0 0 .02-1.71c-.24-.31-.51-.46-1.03-.46-.51 0-.8.26-1.02.6-.21.33-.18.73-.18 1.1H5.75c0-1.38.35-2.25 1.1-2.76.52-.35 1.17-.5 1.93-.5 1 0 1.79.18 2.49.71.64.5.98 1.18.98 2.12 0 .57-.2 1.05-.48 1.44z"></path></svg>
                            </a>
                        </li>
                    </ul>
                </div>
            </header>
        )
    }
}

export default withRouter(Header);
