import React, { useState } from 'react';
import { Link } from 'react-router-dom';

import signoutIcon from '../../../../images/logout.svg'
import Default from '../../../../images/default.png';
import ListRestItem from '../ListRestItem/ListRestItem';

const UserDropdown = props => {
    const [open, setOpen] = useState(false);

    const closeDropdown = () => {
        setTimeout(() => {
            document.querySelector("#user-popover").style.display = "none";
            document.getElementById("root").removeEventListener("click", closeDropdown);
            setOpen(false)
        }, 0)
    }

    const handleOpenUserDropdown = () => {
        if(!open) {
            document.querySelector("#user-popover").style.display = "block";
            document.getElementById("root").addEventListener("click", closeDropdown)
            setOpen(true);
        } else {
            document.querySelector("#user-popover").style.display = "none";
            setOpen(false);
        }
    }

    return (
        <>
            <div
                className="d-flex align-items-center bd50 cs-pointer"
                onClick={handleOpenUserDropdown}
            >
                <img className="bd50" width="40" height="40" src={props.photo || Default} alt="avatar" />
            </div>
            <div id="user-popover" className="s-popover ps-absolute">
                <div className="s-popover--arrow"></div>
                <ul className="list-rest s-anchors">
                    <ListRestItem
                        title="Your profile"
                        href={`/users/profile/${props.uid}`}
                    />
                    <ListRestItem
                        title="Your questions"
                        href="/questions"
                    />
                    <hr className="oc7" />
                    <li>
                        <button
                            className="s-btn s-btn__hovero w-100 text-left d-flex align-items-center"
                            onClick={props.handleSignout}
                        >
                            <img width="18" src={signoutIcon} alt="signout" />
                            <span className="fs-body1 d-block">&nbsp;&nbsp;Signout this account</span>
                        </button>
                    </li>
                </ul>
            </div>
        </>
    )
}

export default UserDropdown;
